         6 + function generateSimpleDiff(oldContent: string, newContent: string, filePath: string): string {
         7 +   const oldLines = oldContent.split('\n');
         8 +   const newLines = newContent.split('\n');
         9 +   
        10 +   let diff = `diff --git a/${filePath} b/${filePath}\n`;
        11 +   diff += `--- a/${filePath}\n`;
        12 +   diff += `+++ b/${filePath}\n`;
        13 +   
        14 +   // Simple line-by-line comparison
        15 +   const maxLines = Math.max(oldLines.length, newLines.length);
        16 +   let contextStart = -1;
        17 +   let removedLines: string[] = [];
        18 +   let addedLines: string[] = [];
        19 +   
        20 +   for (let i = 0; i < maxLines; i++) {
        21 +     const oldLine = oldLines[i];
        22 +     const newLine = newLines[i];
        23 +     
        24 +     if (oldLine !== newLine) {
        25 +       if (contextStart === -1) {
        26 +         contextStart = Math.max(0, i - 3);
        27 +         diff += `@@ -${contextStart + 1},${Math.min(7, oldLines.length - contextStart)} +${contextStart + 1},${Math.min(7, newLines.length - contextStart)} @@\n`;
        28 +         
        29 +         // Add context before
        30 +         for (let j = contextStart; j < i; j++) {
        31 +           if (oldLines[j] !== undefined) {
        32 +             diff += ` ${oldLines[j]}\n`;
        33 +           }
        34 +         }
        35 +       }
        36 +       
        37 +       if (oldLine !== undefined && newLine === undefined) {
        38 +         diff += `-${oldLine}\n`;
        39 +       } else if (oldLine === undefined && newLine !== undefined) {
        40 +         diff += `+${newLine}\n`;
        41 +       } else {
        42 +         diff += `-${oldLine}\n`;
        43 +         diff += `+${newLine}\n`;
        44 +       }
        45 +     } else if (contextStart !== -1 && i < contextStart + 10) {
        46 +       // Add context after changes
        47 +       if (oldLine !== undefined) {
        48 +         diff += ` ${oldLine}\n`;
        49 +       }
        50 +     }
        51 +   }
        52 +   
        53 +   return diff;
        54 + }

